package com.padcmyanmar.padcx.movieassignment.instrumentation_tests

import android.content.Context
import android.util.Log
import androidx.room.Room
import androidx.test.core.app.ApplicationProvider
import androidx.test.internal.runner.junit4.AndroidJUnit4ClassRunner
import com.padcmyanmar.padcx.movieassignment.data.dummy.getDummyMovies
import com.padcmyanmar.padcx.movieassignment.data.vos.MovieVO
import com.padcmyanmar.padcx.movieassignment.persistence.daos.MoviesDao
import com.padcmyanmar.padcx.movieassignment.persistence.db.MoviesDB
import org.junit.After
import org.junit.Before
import org.junit.Test
import org.junit.runner.RunWith

/**
 * Created by Phyoe Sandy Soe Tun
 * on 8/5/2020.
 */
@RunWith(AndroidJUnit4ClassRunner::class)
class DatabaseTest {

    private lateinit var moviesDao: MoviesDao
    private lateinit var db: MoviesDB

    @Before
    fun createDb() {
        val context = ApplicationProvider.getApplicationContext<Context>()
        db = Room.inMemoryDatabaseBuilder(
            context, MoviesDB::class.java
        ).build()
        moviesDao = db.moviesDao()
    }

    @After
    fun closeDb() {
        db.close()
    }

    @Test
    fun insertIntoMoviesTest() {
        val movie = MovieVO()
        movie.movieName = "Titanics"
        movie.movieImage = "data:image/jpeg;base64,"

        moviesDao.insertMovie(movie)
        Log.d("Id is ", moviesDao.getMovieById(movie.id).value?.id.toString())
        assert(moviesDao.getMovieById(movie.id).value?.id == movie.id)
    }
}